import React from 'react'
import Mail from "../svgs/mail.svg";
import Stream from '../images/streams-icon.png'
import { useEffect, useState, useRef } from 'react'
import { Icon } from '@iconify/react';
const Header = () => {
  const [tab, setTab] = useState(1)

  const handleClick = (id) => {
    setTab(id)
  };
  return (
    <div className='header justify-between'>
    <div className='first-header-div flex '>
     <div className='buttons flex'>
      <span className={tab === 1? 'span-1 bg-[white] text-black' : 'span-1 text-white bg-[#3d3f43]'} onClick={() => handleClick(1)} > <img src={Mail} className=" mr-1" style={{width:"8px"}} /> Mail  <Icon icon="iconoir:cancel"  style={{marginTop:"8px"}}/></span>



     </div>
     <div className='buttons flex ml-1.5' >
      <span className={tab === 2? 'span-1 bg-[white] text-black' : 'span-1 text-white bg-[#3d3f43]'} onClick={() => handleClick(2)} > <img src={Stream} className=" mr-1" style={{width:"10px", height:"10px"}} />  Manage <Icon icon="iconoir:cancel"  style={{marginTop:"8px"}}/></span>



     </div>
     <div className='buttons flex ml-1.5'>
      <span className={tab === 3? 'span-1 bg-[white] text-black' : 'span-1 text-white bg-[#3d3f43]'} onClick={() => handleClick(3)} > Streams</span>
      <span className='span-2 bg-[#3d3f43]'  ></span>


     </div>
    </div>

    <div className='second-header-div'>

    </div>
    </div>
  )
}

export default Header
