import React from 'react'
import Mail from "../svgs/mail.svg";
import Stream from '../images/streams-icon.png'
import { useEffect, useState, useRef } from 'react'
import { Icon } from '@iconify/react';
const Header = () => {
  const [tab, setTab] = useState(1)




  const handleClick = event => {
    event.currentTarget.style.backgroundColor = 'white';
    event.currentTarget.style.color = 'black';
  };
  return (
    <div className='header justify-between'>
    <div className='first-header-div flex '>
     <div className='buttons flex' >
      <div>
      <span className='span-1 bg-[#3d3f43]'  > <img src={Mail} className=" mr-1" style={{width:"8px"}} /> Mail </span>
      <span className='span-2 bg-[#3d3f43]' ><Icon icon="iconoir:cancel"  style={{marginTop:"8px"}}/></span>
      </div>

     </div>
     <div className='buttons flex ml-1.5' >
      <span className='span-1 bg-[#3d3f43]' onClick={handleClick} > <img src={Stream} className=" mr-1" style={{width:"10px", height:"10px"}} />  Manage</span>
      <span className='span-2 bg-[#3d3f43]' onClick={handleClick} ><Icon icon="iconoir:cancel"  style={{marginTop:"8px"}}/></span>


     </div>
     <div className='buttons flex ml-1.5'>
      <span className='span-1 bg-[#3d3f43]' onClick={handleClick} > Streams</span>
      <span className='span-2 bg-[#3d3f43]' onClick={handleClick} ><Icon icon="iconoir:cancel"  style={{marginTop:"8px"}}/></span>


     </div>
    </div>

    <div className='second-header-div'>

    </div>
    </div>
  )
}

export default Header
